mapper.select:
SELECT member_name
  FROM member
 WHERE member_id = #{member_id}
   AND password = #{password}

SELECT member_id, member_name, member_email, member_grade
  FROM member
 WHERE member_id = #{member_id}
   AND password = #{password}

SELECT member_id
  FROM member
 WHERE member_name = #{member_name}
   AND member_phone = #{member_phone}

SELECT password
  FROM member
 WHERE member_id = #{member_id}
   AND member_name = #{member_name}
   AND member_email = #{member_email}

mapper.update:
UPDATE member
   SET password = #{newPwd}
 WHERE member_id = #{member_id}
