mapper.select:
SELECT ri.reserve_date as rdate, ci.cafe_address as caddress, ci.cafe_name as cname, ci.title_photo as img, reserve_id, isCancelled
FROM reserve_info ri inner join cafe_info ci
on ri.cafe_id = ci.cafe_id
WHERE ri.member_id = #{id} and ri.reserve_date >= #{nowDate}
ORDER BY reserve_date asc;

SELECT ri.reserve_date as rdate, ci.cafe_address as caddress, ci.cafe_name as cname, ci.title_photo as img, reserve_id, isCancelled
 FROM reserve_info ri inner join cafe_info ci
 on ri.cafe_id = ci.cafe_id
WHERE ri.member_id = #{id} and ri.reserve_date <= #{nowDate} 
ORDER BY reserve_date asc;

mapper.update:
update reserve_info set
isCancelled = 1
where reserve_id = #{rid}
